import * as React from 'react';
import {useState} from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import {Paper} from '@mui/material';
import Button from '@mui/material/Button';
import { DateField } from '@mui/x-date-pickers/DateField';

export default function Student() {
    const paperStyle= {padding: '50px 20px', width:600, margin: '20px auto'}
    const[name,setName]=useState('');
    const[dos,setDos]=useState('');
    const[email,setEmail]=useState('');
    const handleClick=(e)=>{
        e.preventDefault();
        const student={name,dos,email}
        console.log(student);
        fetch("http://localhost:8080/api/v1/student",{
            method:"POST",
            headers:{"Content-Type":"application/json"},
            body:JSON.stringify(student)
        }).then(()=>{
            console.log("Student is added");
        })
    }

    return (
    <Box
      component="form"
      sx={{
        '& > :not(style)': { m: 1, width: '25ch',  },
      }}
      noValidate
      autoComplete="off"
    >
        
        <Paper elevation={3} style={paperStyle}>
            <h1 style={{color:"blue"}}><u>Add Student</u></h1>
            <form className="Student">
                <TextField className='addStudent' id="studentName" label="Student Name" variant="outlined" fullWidth
                value={name}
                onChange={(e)=>setName(e.target.value)} />
                <DateField label="Basic date field" className='addStudent' id="dos" variant="outlined" fullWidth
                value={name}
                onChange={(e)=>setName(e.target.value)} />
                <TextField className='addStudent' id="studentEmail" label="Email" variant="outlined" fullWidth
                value={email}
                onChange={(e)=>setAddress(e.target.value)}/>
                <Button className='addStudent' variant="contained" onClick={handleClick}>Submit</Button>
                {name}
                {address}
            </form>
        </Paper>
    </Box>
  );
}
